import { View, Text, StyleSheet, Dimensions, Image, ScrollView } from "react-native";
import React from "react";
import { useHeaderHeight } from "@react-navigation/elements";
const img = require("../assets/landscape.jpg");
const imgRes = Image.resolveAssetSource(img);
const imgURI = imgRes.uri;

export default function Slide({}) {
	const ratio = imgRes.height / imgRes.width;
	const maxWidth = Dimensions.get("window").width * 0.9;
	const imgWidth = Math.min(imgRes.width, maxWidth);
	const imgHeight = Math.min(imgRes.height, maxWidth * ratio);
	const help = useHeaderHeight();

	return (
		<View style={[styles.main]}>
			<ScrollView
				style={{ flex: 1 }}
				nestedScrollEnabled={true}
				alwaysBounceVertical={false}
				showsVerticalScrollIndicator={false}
				contentContainerStyle={{ alignItems: "center" }}>
				<View style={[styles.titleContainer]}>
					<Text style={[styles.titleText]}>Title</Text>
				</View>
				<View style={[styles.titleContainer, { width: "100%", alignItems: "center" }]}>
					<Image style={{ height: imgHeight, width: imgWidth }} source={{ uri: imgURI }}></Image>
					<View style={[styles.textContainer]}>
						<Text style={[styles.text2]}>
							Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium, laudantium, eveniet
							repudiandae harum nihil, alias iusto vero deserunt voluptatem dolor delectus tempora
							accusamus neque? Cupiditate odit numquam ex quidem incidunt. Lorem ipsum dolor sit amet
							consectetur adipisicing elit. Sint numquam nesciunt aspernatur optio pariatur unde itaque
							quo iure ratione animi eaque, ab perspiciatis incidunt perferendis adipisci quas quam est
							velit. Lorem ipsum dolor sit, amet consectetur adipisicing elit. Vero odit commodi quaerat
							repellendus ipsam similique libero, iste, id unde voluptatum eaque architecto pariatur
							incidunt corrupti quo. Fugit quae praesentium necessitatibus? Lorem ipsum dolor sit amet
							consectetur adipisicing elit. Quisquam fugiat et quos totam beatae repellendus odit
							nesciunt. Blanditiis saepe voluptatem atque corporis nemo fuga esse sit consectetur, culpa
							commodi minima.
						</Text>
					</View>
				</View>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	main: {
		// flex: 1,
		// height: Dimensions.get("screen").height,
		height: 500,
		width: Dimensions.get("screen").width,
		// justifyContent: "center",
		alignItems: "center",
	},
	titleContainer: {
		paddingTop: 20,
	},
	titleText: {
		fontSize: 26,
	},
	background: {
		// paddingTop: 75,
		width: Dimensions.get("screen").width,
		height: "100%",
		justifyContent: "center",
		alignItems: "center",
	},
	textContainer: {
		marginTop: 20,
		padding: 10,
		width: "95%",
		justifyContent: "center",
		alignItems: "center",
		// backgroundColor: "rgba(25,25,25,0.86)",
		// borderRadius: 5,
	},
	text: {
		color: "white",
		fontSize: 20,
		textAlign: "center",
	},
	container: {
		padding: 15,
		flex: 1,
		// backgroundColor: "#101010",
	},
	text2: {
		// color: "white",
		fontSize: 18,
	},
	button: {
		margin: 15,
		padding: 5,
		height: 50,
		width: "60%",
		backgroundColor: "#33BBFF",
		alignItems: "center",
		justifyContent: "center",
		borderRadius: 5,
	},
});
